import {
  StyleSheet,
  Text,
  View,
  ScrollView,
  Image,
  TouchableOpacity,
  FlatList,
  SafeAreaView
} from "react-native";
import React, { useEffect, useState } from "react";

import Post from "./PostCard.js";
import data from "../../assets/data/postdata.json.js";

export default function PostContent({ query }) {
  const [posts, setPosts] = useState(JSON.parse(data));
  const [selectedPost, setSelected] = useState(null);
  console.log(posts);

  const RenderPost = ({ item }) => {
    const bg = item.id === selectedPost ? "#fff" : "#f8f8f8";

    return (
      <Post
        item={item}
        onPress={() => setSelected(item.id)}
        backgroundColor={{ bg }}
      />
    );
  };

  useEffect(() => {
    console.log(query);
    let newPost;
    const filterquery = (q) => {
      if (
        q.post.toLowerCase().includes(query.toLowerCase()) ||
        q.first_name.toLowerCase().includes(query.toLowerCase()) ||
        q.last_name.toLowerCase().includes(query.toLowerCase())
      )
        return true;
      else return false;
    };
    if (query.length > 0) {
      newPost = JSON.parse(data).filter(filterquery);
      setPosts(newPost);
    }
  }, [query]);

  return (
    <SafeAreaView
      style={styles.postContainer}
      contentContainerStyle={{
        flexGrow: 1,
        alignItems: "center",
        width: "100%"
      }}
    >
      <FlatList
        data={posts}
        renderItem={RenderPost}
        keyExtractor={(item) => item.id}
        extraData={selectedPost}
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  postContainer: {
    // flex: 1,
    flexDirection: "row",
    width: "100%"
  }
});
