import {
  StyleSheet,
  Text,
  View,
  ScrollView,
  Image,
  TouchableOpacity,
  FlatList
} from "react-native";
import React, { useEffect, useState } from "react";

import data from "../../assets/data/postdata.json.js";

export default function PostContent({ query }) {
  const [posts, setPosts] = useState(JSON.parse(data));

  useEffect(() => {
    console.log(query);
    let newPost;
    const filterquery = (q) => {
      if (
        q.post.toLowerCase().includes(query.toLowerCase()) ||
        q.first_name.toLowerCase().includes(query.toLowerCase()) ||
        q.last_name.toLowerCase().includes(query.toLowerCase())
      )
        return true;
      else return false;
    };
    if (query.length > 0) {
      newPost = JSON.parse(data).filter(filterquery);
      setPosts(newPost);
    }
  }, [query]);

  return (
    <ScrollView
      style={styles.postContainer}
      contentContainerStyle={{
        flexGrow: 1,
        alignItems: "center",
        width: "100%"
      }}
    ></ScrollView>
  );
}

const styles = StyleSheet.create({
  postContainer: {
    // flex: 1,
    flexDirection: "row",
    width: "100%"
  }
});
