import { StyleSheet, Text, View, ScrollView } from "react-native";
import React, { useEffect, useState } from "react";
import Ionicons from "react-native-vector-icons/Ionicons";

import data from "../../assets/data/testdata.json.js";

export default function PostContent({ query }) {
  const [posts, setPosts] = useState(JSON.parse(data));
  console.log(posts);

  useEffect(() => {
    console.log(query);
    let newPost;
    const filterquery = (q) => {
      if (
        q.post.includes(query) ||
        q.first_name.includes(query) ||
        q.last_name.includes(query)
      )
        return true;
      else return false;
    };
    if (query.length > 0) {
      newPost = JSON.parse(data).filter(filterquery);
      setPosts(newPost);
    }
  }, [query]);

  return (
    <ScrollView
      style={styles.postContainer}
      contentContainerStyle={{
        flexGrow: 1,
        alignItems: "center"
      }}
    >
      {posts.map((d, id) => {
        return (
          <View key={id} style={styles.card}>
            <View>
              <Ionicons name={"ios-person-circle-outline"} size={40} />
            </View>
            <View>
              <Text style={styles.title}>
                {d.first_name} {d.last_name}
              </Text>
              <Text style={styles.post}>{d.post}</Text>
            </View>
            <View>
              <Ionicons name={"heart-outline"} size={40} />
            </View>
          </View>
        );
      })}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  postContainer: {
    flex: 1,
    flexDirection: "row",
    width: "100%"
  },
  card: {
    width: "90%",
    flex: 1,
    flexDirection: "row",
    backgroundColor: "green",
    height: 80,
    marginBottom: 20,
    backgroundColor: "#f8f8f8",
    borderRadius: 6,
    shadowColor: "#13997c",
    shadowOffset: { width: -2, height: 4 },
    shadowOpacity: 0.2,
    shadowRadius: 3
  },
  title: {
    color: "#000"
  },
  post: {
    color: "#000",
    opacity: 0.7
  }
});
