import { StyleSheet, FlatList, SafeAreaView } from "react-native";
import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import data from "../../assets/data/postdata.json.js";
import Post from "./PostCard.js";

import dateFormat from "../../helpers/dateFormat.js";
import { updatePost } from "../../redux/reducers.js";
import { filterByTime } from "../../helpers/sortData.js";

export default function PostContent({ query }) {
  const [selectedPost, setSelected] = useState(null);
  const state = useSelector((state) => state.reduxStore);
  const dispatch = useDispatch();

  useEffect(() => {
    console.log(state);
  }, [state]);

  const RenderPost = ({ item }) => {
    const bg = item.id === selectedPost ? "#ffffff" : "#f8f8f8";
    const validCount = () => {
      return posts.length > 0;
    };
    return (
      <Post
        item={item}
        onPress={() => setSelected(item.id)}
        backgroundColor={{ bg }}
        validCount={validCount}
        query={query}
      />
    );
  };
  let sorted = JSON.parse(data)
    .slice()
    .sort((a, b) => {
      dateFormat(b.date_posted) - dateFormat(a.date_posted);
    });

  console.log(filterByTime(JSON.parse(data)));

  useEffect(() => {
    console.log(query);
    let newPost;
    const filterquery = (q) => {
      if (
        q.post.toLowerCase().includes(query.toLowerCase()) ||
        q.first_name.toLowerCase().includes(query.toLowerCase()) ||
        q.last_name.toLowerCase().includes(query.toLowerCase())
      )
        return true;
      else return false;
    };
    if (query.length > 0) {
      newPost = JSON.parse(data).filter(filterquery);
      dispatch(updatePost(newPost));
    }
  }, [query]);

  return (
    <SafeAreaView
      style={styles.postContainer}
      contentContainerStyle={{
        flexGrow: 1,
        alignItems: "center",
        width: "100%"
      }}
    >
      <FlatList
        data={state.posts}
        renderItem={RenderPost}
        keyExtractor={(item) => item.id}
        extraData={selectedPost}
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  postContainer: {
    // flex: 1,
    flexDirection: "row",
    width: "100%"
  }
});
