import {
  StyleSheet,
  Text,
  View,
  ScrollView,
  Image,
  TouchableOpacity,
  FlatList
} from "react-native";
import React, { useEffect, useState } from "react";
import Ionicons from "react-native-vector-icons/Ionicons";

import { TimeSince } from "../../helpers/timeSince";
import data from "../../assets/data/postdata.json.js";

export default function PostContent({ query }) {
  const [posts, setPosts] = useState(JSON.parse(data));

  useEffect(() => {
    console.log(query);
    let newPost;
    const filterquery = (q) => {
      if (
        q.post.toLowerCase().includes(query.toLowerCase()) ||
        q.first_name.toLowerCase().includes(query.toLowerCase()) ||
        q.last_name.toLowerCase().includes(query.toLowerCase())
      )
        return true;
      else return false;
    };
    if (query.length > 0) {
      newPost = JSON.parse(data).filter(filterquery);
      setPosts(newPost);
    }
  }, [query]);

  const Post = ({ d, onPress, backgroundColor, textColor }) => {
    <TouchableOpacity onPress={onPress} style={[styles.card, backgroundColor]}>
      <View style={styles.profile}>
        <Image
          source={{ uri: d.profile_picture }}
          style={styles.profileimage}
        />
      </View>
      <View style={styles.info}>
        <View style={styles.header} numberOfLines={1}>
          <Text style={styles.name}>
            {d.first_name} {d.last_name}
          </Text>
          <Text style={styles.handle}>@{d.handle}</Text>
          <Text style={styles.date}>
            {posts.length > 0 && TimeSince(d.date_posted)}
          </Text>
        </View>
        <Text style={styles.post}>{d.post}</Text>
        <View style={styles.interactions}>
          <View style={styles.comments}>
            <Ionicons name={"chatbubble-outline"} size={14} />
            <Text style={{ fontSize: 11, paddingLeft: 2 }}>{d.comments}</Text>
          </View>
          <View style={styles.shares}>
            <Ionicons name={"ios-arrow-redo-outline"} size={17} />
            <Text style={{ fontSize: 11, paddingLeft: 2 }}>{d.retweets}</Text>
          </View>
          <View style={styles.likes}>
            <Ionicons name={"heart-outline"} size={16} />
            <Text style={{ fontSize: 11, paddingLeft: 2 }}>{d.likes}</Text>
          </View>
        </View>
      </View>
    </TouchableOpacity>;
  };

  return (
    <ScrollView
      style={styles.postContainer}
      contentContainerStyle={{
        flexGrow: 1,
        alignItems: "center",
        width: "100%"
      }}
    ></ScrollView>
  );
}

const styles = StyleSheet.create({
  postContainer: {
    // flex: 1,
    flexDirection: "row",
    width: "100%"
  },
  card: {
    width: "100%",
    flexDirection: "row",
    backgroundColor: "#f8f8f899",
    marginBottom: 3,
    shadowColor: "#13997c",
    shadowOffset: { width: -2, height: 4 },
    shadowOpacity: 0.2,
    shadowRadius: 10,
    paddingVertical: 10
  },
  profile: {
    width: "20%",
    alignItems: "center"
  },
  profileimage: {
    borderRadius: 50,
    height: 50,
    width: 50
  },
  info: {
    width: "80%"
  },
  header: {
    color: "#000",
    display: "flex",
    flexDirection: "row",
    fontWeight: "bold"
  },
  name: {
    fontWeight: "bold",
    paddingRight: 2
  },
  handle: {
    opacity: 0.8,
    paddingRight: 5
  },
  date: {
    opacity: 0.8
  },
  post: {
    color: "#000",
    opacity: 1
  },
  interactions: {
    paddingTop: 8,
    width: "100%",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "flex-start"
  },
  likes: {
    flexDirection: "row",
    alignItems: "center",
    width: "30%"
  },
  shares: {
    flexDirection: "row",
    alignItems: "center",
    width: "30%"
  },
  comments: {
    flexDirection: "row",
    alignItems: "center",
    width: "30%"
  }
});
