import { StyleSheet, Text, View, ScrollView } from "react-native";
import React, { useEffect, useState } from "react";
import Ionicons from "react-native-vector-icons/Ionicons";

import { TimeSince } from "../../helpers/timeSince";
import data from "../../assets/data/postdata.json.js";

export default function PostContent({ query }) {
  const [posts, setPosts] = useState(JSON.parse(data));

  console.log(posts[0].date_posted.substring(0, 1));
  console.log(posts[0].date_posted.substring(2, 4));
  console.log(posts[0].date_posted.substring(5, 9));

  useEffect(() => {
    console.log(query);
    let newPost;
    const filterquery = (q) => {
      if (
        q.post.includes(query) ||
        q.first_name.includes(query) ||
        q.last_name.includes(query)
      )
        return true;
      else return false;
    };
    if (query.length > 0) {
      newPost = JSON.parse(data).filter(filterquery);
      setPosts(newPost);
    }
  }, [query]);

  return (
    <ScrollView
      style={styles.postContainer}
      contentContainerStyle={{
        flexGrow: 1,
        alignItems: "center",
        width: "100%"
      }}
    >
      {posts.map((d, id) => {
        return (
          <View key={id} style={styles.card}>
            <View style={styles.profile}>
              <Ionicons name={"ios-person-circle-outline"} size={40} />
            </View>
            <View style={styles.info}>
              <View style={styles.title} numberOfLines={1}>
                <Text>
                  {" "}
                  {d.first_name} {d.last_name}
                </Text>
                <Text>@{d.handle}</Text>
                <Text>{TimeSince(d.date_posted)}</Text>
              </View>
              <Text style={styles.post}>{d.post}</Text>
              <Text style={styles.date}>{d.date_posted}</Text>
            </View>
            <View style={styles.likes}>
              <Text style={{ fontSize: 12 }}>{d.likes}</Text>
              <Ionicons name={"heart-outline"} size={20} />
            </View>
          </View>
        );
      })}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  postContainer: {
    // flex: 1,
    flexDirection: "row",
    width: "100%"
  },
  card: {
    width: "100%",
    flexDirection: "row",
    backgroundColor: "#f8f8f899",
    marginBottom: 3,
    shadowColor: "#13997c",
    shadowOffset: { width: -2, height: 4 },
    shadowOpacity: 0.2,
    shadowRadius: 10,
    paddingVertical: 10
  },
  profile: {
    width: "15%",
    paddingLeft: 5
  },
  title: {
    color: "#000",
    flex: 1,
    fontWeight: "bold"
  },
  post: {
    color: "#000",
    opacity: 0.9
  },
  info: {
    width: "70%"
  },
  likes: {
    flex: 1,
    width: "15%",
    justifyContent: "center",
    alignItems: "center"
  }
});
